import logo from './logo.svg';
import './App.css';
import * as React from 'react';
import { useState } from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { BarChart } from '@mui/x-charts/BarChart';
import {useSpring, animated} from 'react-spring';
import { alignProperty } from '@mui/material/styles/cssUtils';
import {Text} from 'react-native';
import { evaluateArgument } from './data_analysis';

const MAX_PKMN = 1025;
var loaded_pkmn = 0;
var inline_loaded = 0;
var startedLoading = false;
var activeID = 1;
var activeMon = [];
var monForms = {};
var inSearch = false;
var activeData = [];
var pokeStats= [];
var statNames = {
  'hp':'HP',
  'attack':'Atk',
  'defense':'Def',
  'special-attack':'SpA',
  'special-defense':'SpD',
  'speed':'Spe'
}
var typeColours = {
  'normal': '#ABAB9B',
  'fire': '#FF4422',
  'water': '#3399FF',
  'electric': '#FFCC33',
  'grass': '#77CC55',
  'ice': '#66CCFF',
  'fighting': '#BB5544',
  'poison': '#AA5599',
  'ground': '#DDBB55',
  'flying': '#8899FF',
  'psychic': '#FF5599',
  'bug': '#AABB22',
  'rock': '#BBAA66',
  'ghost': '#6666BB',
  'dragon': '#7766EE',
  'dark': '#775544',
  'steel': '#AAAABB',
  'fairy': '#EE99EE'
}
var statNameVals = [
  'HP',
  'Atk',
  'Def',
  'SpA',
  'SpD',
  'Spe'
]
var form_whitelist = 
    {
        "25": [1, 2, 4, 5, 14, 16],
        "658": [2],
        "718": [1,2,3],
        "735": [],
        "738": [],
        "743": [],
        "752": [],
        "754": [],
        "758": [],
        "774": [7,8,9,10,11,12],
        "777": [],
        "778": [],
        "784": [],
        "1007": [],
        "1008": []
    }
const delay = ms => new Promise(res => setTimeout(res, ms));

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));




async function getData(ID)
{
  try
  {
    var response = await fetch("data/api/"+ID+"/api.json").then((response) => response.json());
    var spec_response = await fetch("data/api/"+ID+"/species.json").then((response) => response.json())
    var spriteURL = "data/sprites/"+ID+"/front_default.png"
    var data = response;
    var spec_data = spec_response;
