import {Text} from 'react-native';
import { evaluateArgument } from './data_analysis';

const MAX_PKMN = 1025;
var loaded_pkmn = 0;
var inline_loaded = 0;
var startedLoading = false;
var activeID = 1;
var activeMon = [];
var monForms = {};
var inSearch = false;
var activeData = [];
var pokeStats= [];
