import logo from './logo.svg';
import './App.css';
import * as React from 'react';
import { useState } from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import { BarChart } from '@mui/x-charts/BarChart';
import {useSpring, animated} from 'react-spring';
import { alignProperty } from '@mui/material/styles/cssUtils';
import {Text} from 'react-native';

const MAX_PKMN = 1025;
var loaded_pkmn = 0;
var inline_loaded = 0;
var startedLoading = false;
var activeID = 1;
var activeMon = [];
var inSearch = false;
var activeData = [];
var pokeStats= [];
var statNames = {
  'hp':'HP',
  'attack':'Atk',
  'defense':'Def',
  'special-attack':'SpA',
  'special-defense':'SpD',
  'speed':'Spe'
}
var typeColours = {
  'normal': '#ABAB9B',
  'fire': '#FF4422',
  'water': '#3399FF',
  'electric': '#FFCC33',
  'grass': '#77CC55',
  'ice': '#66CCFF',
  'fighting': '#BB5544',
  'poison': '#AA5599',
  'ground': '#DDBB55',
  'flying': '#8899FF',
  'psychic': '#FF5599',
  'bug': '#AABB22',
  'rock': '#BBAA66',
  'ghost': '#6666BB',
  'dragon': '#7766EE',
  'dark': '#775544',
  'steel': '#AAAABB',
  'fairy': '#EE99EE'
}
var statNameVals = [
  'HP',
  'Atk',
  'Def',
  'SpA',
  'SpD',
  'Spe'
]
const delay = ms => new Promise(res => setTimeout(res, ms));

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));




async function getData(ID)
{
  try
  {
    var response = await fetch("data/api/"+ID+"/api.json").then((response) => response.json());
    var spec_response = await fetch("data/api/"+ID+"/species.json").then((response) => response.json())
    var spriteURL = "data/sprites/"+ID+"/front_default.png"
    var data = response;
    var spec_data = spec_response;
    return [data, spec_data, spriteURL, ID];
  }
  catch(e)
  {
    console.log(e);
  }
  
}

function titleCase(/**@type String */str)
{
    /**@type String */
    var base = str.toLowerCase()
    return base.charAt(0).toUpperCase() + base.slice(1);
}

function splitTitleCase(/**@type String */str)
{
    var parts = str.split("-");
    var final = ""
    for(var i = 0; i < parts.length; i++)
    {
        final += titleCase(parts[i]);
        if(i < parts.length - 1)
        {
            final += " ";
        }
    }
    return final;
}


function pokeSprite(ID)
{
  return "data/sprites/"+ID+"/front_default.png";
}

function search(query)
{
  console.log("query:",query);
}



function pokeIcon(ID)
{
  var pokedata = data[ID]
  console.log(pokedata)
  return (
  <Grid item xs={2} sm={4} md={4} key={ID} id={ID}>
    <Item><img src={process.env.PUBLIC_URL + pokedata[2]} style={{alignContent: "center"}} alt="pokemon data" key={ID}></img>{titleCase(pokedata[0].name)}</Item>
  </Grid>
  );
}

class LiveGrid extends React.Component {
  constructor(props) {
      super(props)
      this.state = { maxLoad: 0};
  }

  updateGrid = () => {

    this.setState({maxLoad: loaded_pkmn});

  }

render() {
    
  return (
    <Grid container spacing={1}>
    {data.map(poke => 
    <Grid item xs={1} sm={4} md={1.5}>
      <Item>
      <img src={process.env.PUBLIC_URL + poke[2]} style={{alignContent: "center"}} alt="pokemon data"></img>
      <h3 style={{textAlign: "center"}}>{titleCase(poke[0].name)}</h3>
      </Item>
    </Grid>)}
  </Grid>
  )
}
}

function activateOverlay(ID, updateMethod)
{
  if(activeID == ID)
  {
    activeMon = [];
    activeID = 0;
    updateMethod(activeMon);
    return;
  }
  console.log('current activeMon', activeMon);
  if(activeMon.length == 0)
  {
    var p = document.querySelector('.pokePanel');
    console.log("panel",p);
    p.style.animationName = 'none';
    requestAnimationFrame(() => {p.style.animationName = ''});
  }
  activeID = ID;
  activeMon = [data[activeID-1]];
  updateMethod(activeMon);
}

var data = []
async function load_data(updateLoader, lD)
{
  if(startedLoading)
  {
    return;
  }
  startedLoading = true;
  for(var i = 1; i <= MAX_PKMN; i++)
  {
    if(i > loaded_pkmn)
    {
      loaded_pkmn++;
      var mon_data = await getData(i);
      data.push(mon_data);
      if(inSearch == false)
      {
        activeData = data;
      }
      updateLoader(data);
      if(activeMon.length > 0)
      {
        await delay(200);
      }
      else
      {
        await delay(2);
      }
      
    }
  }
}



function App() {
/*   console.log(data);
  var grid_rows = []
  for(var i = 1; i <= MAX_PKMN; i++)
  {
    grid_rows.push(pokeIcon(i));
  } */
  const [loadedData, setLoad] = useState([]);
  const [acMon, setMon] = useState([]);
  
  load_data(setLoad, loadedData);
  console.log(inline_loaded);
  if(inline_loaded != data.length)
  {
    setLoad(data);
    inline_loaded = data.length; 
    console.log("inline:",inline_loaded, loadedData);
  }
  React.useEffect(() => {
    console.log("loadedData: ", loadedData);
  })
  var loadingText = <h3></h3>;
  if(loaded_pkmn < MAX_PKMN)
  {
    loadingText = <h3 style={{textAlign: "left", position: "fixed", paddingLeft: "3%", zIndex: 11}}>Loading: {Math.round(100*loaded_pkmn/MAX_PKMN)}%</h3>;
  }

  var pokeiconPanel = <p></p>;
  if(loaded_pkmn >= activeID && activeMon.length > 0)
  {
    var pokestats = []
    var curmon = activeMon[0];
    for(var i = 0; i < 6; i++)
    {
      pokestats.push(curmon[0].stats[i].base_stat);
    }
    /** @type {Array} */
    var poketypes = curmon[0].types;
    var pokeabilities = curmon[0].abilities;
    console.log('pokestats',pokestats);
    pokeiconPanel = activeMon.map(mon => 
      <Item style={{display:"grid"}}>
        <Item style={{width:"5%", justifySelf: "right"}} className='button-style' fontFamily="bwFont" onClick={activateOverlay.bind(this, activeID, setMon)}>Close</Item>
        
        <Grid container style={{justifyContent: "center", width:"100%", display:"grid", gridAutoFlow: "column", gridAutoRows: "max-content", gridAutoColumns: "33%"}} columns={3} row={1}>
        <Grid item style={{width:"100%", alignSelf:"center"}}> 
            <BarChart
            yAxis={[{ scaleType: 'band', data: statNameVals, max:225}]}
            xAxis={[{max:225}]}
            series={[{data: pokestats, color: "purple"}]}
            layout="horizontal"
            width={window.innerHeight/1.5}
            height={window.innerHeight/5}/>
          </Grid>
          
          <Grid item>
            <img src={process.env.PUBLIC_URL + mon[2]} style={{justifySelf: "center", imageRendering: "pixelated", width:"40%"}} alt="pokemon data"></img>
            
          </Grid>
          <Grid item>
              <h1 fontFamily={"bwFont"}><Text style={{fontFamily: "bwFont", fontSize: "32px"}}>{titleCase(mon[0].name)}</Text></h1>
              <Text style={{fontSize: "12px", color:("black"), fontFamily: "bwFont"}}>Type:</Text>
              <br></br>
              {poketypes.map(ptype => 
                <Text style={{fontSize: "24px", color:typeColours[ptype.type.name], fontFamily: "bwFont"}}>{titleCase(ptype.type.name) + "  "}</Text>
                )}
              <br></br>
              <br></br>
              <Text style={{fontSize: "12px", color:("black"), fontFamily: "bwFont"}}>Abilities:</Text>
              <br></br>
              {pokeabilities.map(pabl => 
                <Text style={{fontSize: "18px", color:(pabl.is_hidden == false ? "black" : "#C8AE0F"), fontFamily: "bwFont"}}>{splitTitleCase(pabl.ability.name) + "   "}</Text>
                )}
          </Grid>
        </Grid>
     </Item>)
  }

  return (
    <div className="App" style={{display:"grid"}}>
      <input style={{borderRadius: "20px", fontFamily: "bwFont", zIndex:12, position: 'fixed', justifySelf: "center", borderWidth:"1px", width:"75%", margin:"auto", display:"block", transform: "translate(0px, 10px)", paddingLeft: "20px"}}
      type="submit" onChange={search.bind(this, "hi")}/>
      <div>
        {loadingText}
      </div>
      <div className='pokePanel' fontFamily={"bwFont"} style={{zIndex: 10}}>
        {pokeiconPanel}
      </div>
      <br></br>
      <h2 style={{textAlign: "center"}}>JSDex</h2>
      <body>
      <Grid container spacing={1}>
        {data.map(poke => 
        
        <Grid item xs={4} sm={2} md={1.5} lg={1} key={poke[3]} className='hover-style'>
          <Item style={{}} className='load-style' onClick={activateOverlay.bind(this, poke[3], setMon)}>
          <img src={process.env.PUBLIC_URL + poke[2]} style={{alignContent: "center"}} alt="pokemon data"></img>
          </Item>
        </Grid>
        )}
      </Grid>
      </body>
    </div>
  );
}



export default App;
